async function processContactForm(e){e.preventDefault();const t=e.target,n=new FormData(t),a=validateContactFormData(n),r=t.querySelector(".error-message"),s=t.querySelector(".success-message"),o=t.querySelector(".submit-button");if(r.innerHTML="",s.innerHTML="",a)r.innerHTML=a;else{o.disabled=!0;try{const e=await sendMessage(n);e.ok?(t.reset(),s.innerHTML="Your message has been sent successfully"):r.innerHTML="Failed to send your message"}catch(e){r.innerHTML="Failed to send your message"}o.disabled=!1}}async function sendMessage(e){return await fetch("/",{method:"POST",headers:{"Content-Type":"application/x-www-form-urlencoded"},body:new URLSearchParams(e).toString()})}function validateContactFormData(e){const t=Object.fromEntries(e.entries());return t.name.trim().length<2?"Please enter a valid name":t.email.trim().match(/.+\@.+\..+/)?t.subject.trim().length<2?"Please enter a valid subject":t.content.trim().length<2?"Please enter a valid message":0!=t.mobile.trim().length&&t.mobile.trim().length<9?"Please enter a valid mobile number":"":"Please enter a valid email"}document.getElementById("contact-form").addEventListener("submit",processContactForm);